Replacements.SerialPrintout:
float xData;
float yData;
float zData;
ADXL362.sampleXYZT();
xData = ADXL362.getXGees();
yData = ADXL362.getYGees();
zData = ADXL362.getZGees();

Serial.print("X = ");
Serial.print(xData);
Serial.print(" g Y = ");
Serial.print(yData);
Serial.print(" g Z = ");
Serial.print(zData);
Serial.println(" g");

Replacements.Serial1Printout:
