float altitude = MPL3115A2.readAltitude();
float temp = MPL3115A2.readTemp();
Serial.print("Pressure = ");
Serial.print(pressue);
Serial.print(" Pa Altitude = ");
Serial.print(altitude);
Serial.print(" M temperature = ");
Serial.print(temp);
Serial.println(" C");

